from sqlite3 import dbapi2
from App import db
from App import app
#Creates Users Table
class Users(db.Model):
    userid = db.Column(db.Integer, primary_key=True)
    username = db.Column(db.String, unique=True)
    password = db.Column(db.String)

class UsersFunctions(Users):
    #Checks if username can be found in database
    #Returns true if found, returns false if not found
    def checkUsernameExists(username):
        user = Users.query.filter_by(username)
        if user == username:
            return False
        else:
            return True


#Creates Folders Table
class Folders(db.Model):
    folderID = db.Column(db.Integer, primary_key=True)
    folderName = db.Column(db.String)
    userID = db.Column(db.Integer, foreign_key=True)
class FoldersFunctions(Folders):
    pass

