import sqlite3

con = sqlite3.connect('users.db') #Establishes connection to the Database

cur = con.cursor() #Cursor tells database what to do


#"rowid acts as primary key for all rows"
class createTables:
    #Create User Table
    def createUsersTable():
        cur.execute("""CREATE TABLE users (
            username text,
            password text
        )
        """)
    #Create Folder Table
    def createFoldersTable():
        cur.execute("""CREATE TABLE folders (
            folderName text,
            folderOwner text,
            FOREIGN KEY(folderOwner) REFERENCES users(username)
        )
            
        """)
    #Create Articles Table    
    def createArticlesTable():
        cur.execute("""
            articleTitle text,
            articleKeyWords text,
            articleNotes text,
            articleSummary text,
            articleFolderIn int,
            FOREIGN KEY(articleFolderIn) REFERENCES folders(rowid)
        """) 

class usersTableFunctions:
    def returnContentsofTable():
        cur.execute("SELECT * FROM users")
        cur.fetchall()

class foldersTableFunctions:
    def returnContentsofTable():
        cur.execute("SELECT * FROM folders")
        cur.fetchall()
class articlesTableFunctions:
    def returnContentsofTable():
        cur.execute("SELECT * FROM articles")
        cur.fetchall()
print(usersTableFunctions.returnContentsofTable())


#Commit changes the DB
con.commit()
#Close connection to DB
cur.close()